const Part =(part)=>{
  return(
    <div>
      <p>{part.name} {part.exercises}</p>
    </div>
  )
}

const Header =(head)=>{
  return(
    <div>
      <h1>{head.course}</h1>
    </div>
  )
}

const Content =(items)=>{
  return(
    <div>
      <Part part={items[0]}/>
      <Part part={items[1]}/>
      <Part part={items[2]}/>
    </div>
  )
}

const Total =(total)=>{
  return(
    <div>
        <p>
          Number of exercises {total[0].exercises + total[1].exercises +total[2].exercises}
        </p>
    </div>
  )
}

const App =()=> {
  const course = "Half Stack application development"
  const parts = [
    {
      name: "Fundamentals of React",
      exercises: 10
    },
    {
      name: "Using props to pass data",
      exercises: 7
    },
    {
      name: "State of a component",
      exercises: 14
    }
  ]

  return (
    <>
      <Header course={course}/>
      <Content items={parts} />
      <Total total={parts} />
    </>
  );
}

export default App
